package com.topad.view.customviews;import android.content.Context;import android.text.TextUtils;import android.view.Gravity;import android.view.LayoutInflater;import android.view.View;import android.view.View.OnClickListener;import android.widget.Button;import android.widget.LinearLayout;import android.widget.TextView;import com.topad.R;public class DialogContentView {	View mView;	TextView tv_title,tv_message;	LinearLayout lin_message,lin_bottom;	Button btn_ok,btn_cancel;			public DialogContentView(Context context){		mView= LayoutInflater.from(context).inflate(R.layout.dialog_view, null);		init();	}		private void init(){		tv_title=(TextView)getView().findViewById(R.id.dv_tv_title);		tv_message=(TextView)getView().findViewById(R.id.dv_tv_message);		lin_message=(LinearLayout)getView().findViewById(R.id.dv_lin_message);		lin_bottom=(LinearLayout)getView().findViewById(R.id.dv_lin_bottom);		btn_ok=(Button)getView().findViewById(R.id.dv_btn_ok);		btn_cancel=(Button)getView().findViewById(R.id.dv_btn_cancel);				tv_title.setVisibility(View.GONE);		tv_message.setVisibility(View.GONE);		lin_message.setVisibility(View.GONE);		lin_bottom.setVisibility(View.GONE);		btn_ok.setVisibility(View.GONE);		btn_cancel.setVisibility(View.GONE);	}		public void setTitle(String str){		tv_title.setText(str);		tv_title.setVisibility(View.VISIBLE);	}		public void setMessage(String str){		tv_message.setText(str);		lin_message.setVisibility(View.VISIBLE);		tv_message.setVisibility(View.VISIBLE);	}	public void setMessageLeft(String str){		tv_message.setText(str);		lin_message.setVisibility(View.VISIBLE);		tv_message.setGravity(Gravity.LEFT);		tv_message.setVisibility(View.VISIBLE);	}	public void setOkListener(OnClickListener clicklistener,String str){		if(!TextUtils.isEmpty(str)){			btn_ok.setText(str);		}		btn_ok.setOnClickListener(clicklistener);		lin_bottom.setVisibility(View.VISIBLE);		btn_ok.setVisibility(View.VISIBLE);	}		public void setCancelListener(OnClickListener clicklistener,String str){		if(!TextUtils.isEmpty(str)){			btn_cancel.setText(str);		}		btn_cancel.setOnClickListener(clicklistener);		lin_bottom.setVisibility(View.VISIBLE);		btn_cancel.setVisibility(View.VISIBLE);	}			public View getView(){		return mView;	}}